import React, { useState } from 'react';
import { FaSignInAlt } from 'react-icons/fa';

export function LoginPage({ onLogin, onCancel }) {
    const [credentials, setCredentials] = useState({
        username: '',
        password: ''
    });
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCredentials(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setError('');

        // Вызываем функцию-заглушку для проверки учетных данных
        const isAuthenticated = mockAuthCheck(credentials.username, credentials.password);

        if (isAuthenticated) {
            onLogin();
        } else {
            setError('Неверное имя пользователя или пароль');
        }
    };

    // Функция-заглушка для проверки учетных данных
    const mockAuthCheck = (username, password) => {
        // В реальном приложении здесь будет запрос к серверу
        return username === 'admin' && password === 'password';
    };

    return (
        <div className="login-page">
            <form onSubmit={handleSubmit} className="login-form">
                <h2>Вход в систему</h2>

                <div className="form-group">
                    <label htmlFor="username">Имя пользователя:</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={credentials.username}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="password">Пароль:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={credentials.password}
                        onChange={handleChange}
                        required
                    />
                </div>

                {error && <div className="error-message">{error}</div>}

                <div className="form-actions">
                    <button type="submit" className="login-btn">
                        <FaSignInAlt size={16} />
                        Войти
                    </button>
                    <button type="button" className="cancel-btn" onClick={onCancel}>
                        Отмена
                    </button>
                </div>
            </form>
        </div>
    );
}
