import {useState} from 'react';
import './Login.css'; // Импорт стилей
import React from 'react';

export function Login() {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
    });

    const handleSubmit = function (e) {
        e.preventDefault();
        console.log('Form submitted:', formData);
    };

    const handleInputChange = function (e) {
        const {id, value} = e.target;
        setFormData(prev => ({...prev, [id]: value}));
    };

    return (
        <div className="form-overlay">
            <div className="form-container">
                <h1 className="form-title">Добро пожаловать!</h1>
                <form onSubmit={handleSubmit} className="form">
                    {createInputField('Name', 'Имя', formData.name, handleInputChange, 'text')}
                    {createInputField('LastName', 'Фамилия', formData.name, handleInputChange, 'text')}
                    {createInputField('Surname', 'Отчество', formData.name, handleInputChange, 'text')}
                    {createInputField('Login', 'Логин', formData.name, handleInputChange, 'login')}
                    {createInputField('Password', 'Пароль', formData.name, handleInputChange, 'password')}
                    {createInputField('Email', 'Почта', formData.name, handleInputChange, 'email')}
                    {createInputField('Token', 'Токен', formData.password, handleInputChange, 'token')}
                    <button type="submit" className="submit-button">
                        Вход
                    </button>
                </form>
            </div>
        </div>
    );
}

// Вспомогательная функция для создания полей ввода
function createInputField(id, label, value, onChange, type = 'text') {
    return (
        <div className="input-group">
            <label htmlFor={id} className="input-label">
                {label}
            </label>
            <input
                type={type}
                id={id}
                value={value}
                onChange={onChange}
                className="input-field"
                required
            />
        </div>
    );
}
