import { useState } from 'react';
import './Home.css'; // Импорт стилей
import React from 'react';

export function Home() {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
    });

    const handleSubmit = function(e) {
        e.preventDefault();
        console.log('Form submitted:', formData);
    };

    const handleInputChange = function(e) {
        const { id, value } = e.target;
        setFormData(prev => ({ ...prev, [id]: value }));
    };

    return (
        <div className="form-container">
            <h1 className="form-title">Добро пожаловать!</h1>
            <form onSubmit={handleSubmit} className="form">
                {createInputField('Login', 'Логин', formData.name, handleInputChange, 'login')}
                {createInputField('Password', 'Пароль', formData.password, handleInputChange, 'password')}
                <button type="submit" className="submit-button">
                    Рагестрация
                </button>
            </form>
        </div>
    );
}

// Вспомогательная функция для создания полей ввода
function createInputField(id, label, value, onChange, type = 'text') {
    return (
        <div className="input-group">
            <label htmlFor={id} className="input-label">
                {label}
            </label>
            <input
                type={type}
                id={id}
                value={value}
                onChange={onChange}
                className="input-field"
                required
            />
        </div>
    );
}
