import React, {useEffect, useRef, useState} from "react";
import {FaBell, FaSignOutAlt} from "react-icons/fa";
import logo from "./TNS_Energo_logo.png";
import './HederUserPA.css';

export function HederUserPA(){
    const [notificationsList, setNotificationsList] = useState([
        { id: 1, text: "Новое сообщение", read: false },
        { id: 2, text: "Обновление системы", read: false },
        { id: 3, text: "Обновление безопасности", read: false },
        { id: 4, text: "Новые документы", read: false },
        { id: 5, text: "Изменения в расписании", read: false }
    ]);

    const unreadCount = notificationsList.filter(n => !n.read).length;
    const [showNotifications, setShowNotifications] = useState(false);
    const notificationsRef = useRef(null);
    const dropdownRef = useRef(null);

    const handleLogout = () => {
        console.log('User logged out');
    };

    // Блокировка прокрутки основного контента
    useEffect(() => {
        if (showNotifications) {
            document.body.style.overflow = 'hidden';
        } else {
            document.body.style.overflow = 'auto';
        }

        return () => {
            document.body.style.overflow = 'auto';
        };
    }, [showNotifications]);

    const toggleNotifications = () => {
        const newShowState = !showNotifications;
        setShowNotifications(newShowState);

        if (newShowState && unreadCount > 0) {
            setNotificationsList(notificationsList.map(n => ({ ...n, read: true })));

            // Прокручиваем к началу при открытии
            setTimeout(() => {
                if (dropdownRef.current) {
                    dropdownRef.current.scrollTop = 0;
                }
            }, 0);
        }
    };

    // Закрытие уведомлений при клике вне области
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (notificationsRef.current && !notificationsRef.current.contains(event.target)) {
                if (!event.target.closest('.notification-btn')) {
                    setShowNotifications(false);
                }
            }
        };

        if (showNotifications) {
            document.addEventListener('mousedown', handleClickOutside);
        } else {
            document.removeEventListener('mousedown', handleClickOutside);
        }

        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [showNotifications]);

    return (
        <header className="header">
            <button
                className="notification-btn"
                onClick={toggleNotifications}
                aria-label="Уведомления"
                aria-expanded={showNotifications}
            >
                <FaBell size={20} />
                {unreadCount > 0 && (
                    <span className="notification-badge">{unreadCount}</span>
                )}
            </button>

            <div className="logo-container">
                <img src={logo} alt="Логотип ТНС Энерго" className="logo" />
            </div>

            <button
                className="logout-btn"
                onClick={handleLogout}
                aria-label="Выход"
            >
                <FaSignOutAlt size={20} />
            </button>

            {showNotifications && (
                <div
                    className="notification-dropdown"
                    ref={dropdownRef}
                >
                    {notificationsList.length > 0 ? (
                        <ul>
                            {notificationsList.map(notification => (
                                <li
                                    key={notification.id}
                                    className={notification.read ? 'read' : 'unread'}
                                >
                                    {notification.text}
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>Новых уведомлений нет</p>
                    )}
                </div>
            )}
        </header>
    );
}
