import { FaBell, FaSignOutAlt } from 'react-icons/fa';
import './UserPA.css'; // Импорт стилей
import logo from './logo.png'; // Импортируем изображение логотипа
import React, {useState} from 'react';

export function UserPA() {
    const [notifications, setNotifications] = useState(3); // Пример количества уведомлений
    const [showNotifications, setShowNotifications] = useState(false);

    const handleLogout = () => {
        // Логика выхода
        console.log('User logged out');
    };

    const toggleNotifications = () => {
        setShowNotifications(!showNotifications);
        if (!showNotifications && notifications > 0) {
            setNotifications(0); // Сбрасываем счетчик при просмотре
        }
    };

    return (
        <header className={"header"}>
            <div className={"logo"}>
                <img>
                    logo
                </img>
            </div>

            <div className={"controls"}>
                <button
                    className={"notificationBtn"}
                    onClick={toggleNotifications}
                    aria-label="Notifications"
                >
                    <FaBell size={20} />
                    {notifications > 0 && (
                        <span className={"badge"}>{notifications}</span>
                    )}
                </button>

                <button
                    className={"logoutBtn"}
                    onClick={handleLogout}
                    aria-label="Logout"
                >
                    <FaSignOutAlt size={20} />
                </button>
            </div>

            {/* Выпадающее меню уведомлений */}
            {showNotifications && (
                <div className={"notificationDropdown"}>
                    <p>Новых уведомлений нет</p>
                    {/* Здесь можно отображать список уведомлений */}
                </div>
            )}
        </header>
    );
}
