import { useEffect, useRef } from 'react';
import { X } from 'lucide-react';
import './Modal.css';
import React from 'react';

export function Modal({
                          isOpen,
                          onClose,
                          title,
                          children,
                          primaryAction,
                          secondaryAction,
                      }) {
    const modalRef = useRef(null);

    useEffect(() => {
        const handleEscape = (e) => {
            if (e.key === 'Escape') onClose();
        };

        if (isOpen) {
            document.addEventListener('keydown', handleEscape);
            document.body.style.overflow = 'hidden';
        }

        return () => {
            document.removeEventListener('keydown', handleEscape);
            document.body.style.overflow = 'unset';
        };
    }, [isOpen, onClose]);

    if (!isOpen) return null;

    return React.createElement(
        'div',
        { className: 'modal-overlay' },
        React.createElement(
            'div',
            { className: 'modal-container' },
            React.createElement(
                'div',
                {
                    className: 'modal-backdrop',
                    onClick: onClose
                }
            ),
            React.createElement(
                'div',
                {
                    ref: modalRef,
                    className: 'modal-content'
                },
                [
                    createModalHeader(title, onClose),
                    React.createElement(
                        'div',
                        { className: 'modal-body' },
                        children
                    ),
                    createModalFooter(primaryAction, secondaryAction)
                ].filter(Boolean)
            )
        )
    );
}

function createModalHeader(title, onClose) {
    return React.createElement(
        'div',
        { className: 'modal-header' },
        [
            React.createElement(
                'h3',
                { className: 'modal-title' },
                title
            ),
            React.createElement(
                'button',
                {
                    onClick: onClose,
                    className: 'modal-close-button'
                },
                React.createElement(X, { className: 'modal-close-icon' })
            )
        ]
    );
}

function createModalFooter(primaryAction, secondaryAction) {
    if (!primaryAction && !secondaryAction) return null;

    return React.createElement(
        'div',
        { className: 'modal-footer' },
        [
            secondaryAction && createActionButton(secondaryAction, 'secondary'),
            primaryAction && createActionButton(primaryAction, 'primary')
        ].filter(Boolean)
    );
}

function createActionButton(action, type) {
    return React.createElement(
        'button',
        {
            onClick: action.onClick,
            className: `modal-button modal-button-${type}`
        },
        action.label
    );
}
