import { FaBell, FaSignOutAlt } from 'react-icons/fa';
import './UserPA.css'; // Импорт стилей
import logo from './logo.png'; // Импортируем изображение логотипа
import React, {useState} from 'react';

export function UserPA() {
    const [notificationsList, setNotificationsList] = useState([
        { id: 1, text: "Новое сообщение", read: false },
        { id: 2, text: "Обновление системы", read: false }
    ]);
    const unreadCount = notificationsList.filter(n => !n.read).length;
    const [showNotifications, setShowNotifications] = useState(false);

    const handleLogout = () => {
        // Логика выхода
        console.log('User logged out');
    };

    const toggleNotifications = () => {
        setShowNotifications(!showNotifications);
        if (!showNotifications && unreadCount > 0) {
            setNotificationsList(0); // Сбрасываем счетчик при просмотре
        }
    };

    return (
        <header className="header">
            {/* Кнопка уведомлений (левый верхний угол) */}
            <button
                className="notification-btn"
                onClick={toggleNotifications}
                aria-label="Уведомления"
            >
                <FaBell size={20} />
                {unreadCount > 0 && (
                    <span className="notification-badge">{unreadCount}</span>
                )}
            </button>

            {/* Логотип по центру */}
            <div className="logo-container">
                <img src={logo} alt="Логотип компании" className="logo" />
            </div>

            {/* Кнопка выхода (правый верхний угол) */}
            <button
                className="logout-btn"
                onClick={handleLogout}
                aria-label="Выход"
            >
                <FaSignOutAlt size={20} />
            </button>
        </header>
    );
}
