import React from 'react';
import { useState } from 'react';
import { Modal } from '../components/Modal';
import './ModalPage.css';

export function ModalPage() {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const modalContent = React.createElement(
        'div',
        { className: 'modal-content-container' },
        [
            React.createElement(
                'p',
                { className: 'modal-text' },
                'This is an example modal that can be customized with different content and actions. ' +
                'The modal handles keyboard events (Escape to close) and click-outside behavior.'
            ),
            React.createElement(
                'p',
                { className: 'modal-text' },
                'You can add any content here, including forms, images, or other components.'
            )
        ]
    );

    return React.createElement(
        'div',
        { className: 'modal-page-container' },
        [
            React.createElement(
                'h1',
                { className: 'modal-page-title' },
                'Modal Example'
            ),
            React.createElement(
                'button',
                {
                    className: 'modal-trigger-button',
                    onClick: () => setIsModalOpen(true)
                },
                'Open Modal'
            ),
            React.createElement(
                Modal,
                {
                    isOpen: isModalOpen,
                    onClose: () => setIsModalOpen(false),
                    title: 'Example Modal',
                    primaryAction: {
                        label: 'Confirm',
                        onClick: () => {
                            console.log('Confirmed');
                            setIsModalOpen(false);
                        }
                    },
                    secondaryAction: {
                        label: 'Cancel',
                        onClick: () => setIsModalOpen(false)
                    },
                    children: modalContent
                }
            )
        ]
    );
}
