import React, {useState} from 'react';
import {Registration} from './Page/Registration';
import {LoginPage} from './Page/LoginPage';
import {UserPA} from './Page/UserPA';
import {AdminPA} from './Page/AdminPA';
import './Page/HederUserPA.css';
import logo from "./Page/TNS_Energo_logo.png";
import {FaSignInAlt, FaTimes, FaUser} from "react-icons/fa";


function App() {
    const [showLoginModal, setShowLoginModal] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLoginClick = () => {
        if (isLoggedIn) {
            // Логика выхода
            setIsLoggedIn(false);
        } else {
            setShowLoginModal(true);
        }
    };

    const handleCloseModal = () => {
        setShowLoginModal(false);
    };

    const handleLoginSubmit = (e) => {
        e.preventDefault();
        // Заглушка для входа - в реальном приложении здесь будет проверка учетных данных
        setIsLoggedIn(true);
        setShowLoginModal(false);
    };

    return (
        <>
            <header className="header">
                <div className="logo-container">
                    <img src={logo} alt="Логотип ТНС Энерго" className="logo" />
                </div>
                <button
                    className="login-btn"
                    onClick={handleLoginClick}
                    aria-label={isLoggedIn ? "Выход" : "Вход"}
                >
                    {isLoggedIn ? <FaUser size={18} /> : <FaSignInAlt size={18} />}
                    <span className="login-text">
            {isLoggedIn ? "Профиль" : "Войти"}
          </span>
                </button>
            </header>

            {/* Модальное окно входа */}
            {showLoginModal && (
                <div className="login-modal-overlay">
                    <div className="login-modal">
                        <button className="close-modal-btn" onClick={handleCloseModal}>
                            <FaTimes />
                        </button>
                        <h3>Вход в систему</h3>
                        <form onSubmit={handleLoginSubmit}>
                            <div className="form-group">
                                <label htmlFor="username">Логин:</label>
                                <input
                                    type="text"
                                    id="username"
                                    placeholder="Введите ваш логин"
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="password">Пароль:</label>
                                <input
                                    type="password"
                                    id="password"
                                    placeholder="Введите ваш пароль"
                                    required
                                />
                            </div>
                            <button type="submit" className="submit-btn">
                                Войти
                            </button>
                        </form>
                    </div>
                </div>
            )}

            {/* Основное содержимое приложения */}
            <main className="main-content">
                {isLoggedIn ? (
                    <UserPA /> // или <AdminPA /> в зависимости от роли
                ) : (
                    <div className="welcome-message">
                        <h2>Добро пожаловать в систему ТНС Энерго</h2>
                        <p>Пожалуйста, войдите в систему для доступа к функциям</p>
                    </div>
                )}
            </main>
        </>
    );
}

export default App;
