import React, { useState, useRef, useEffect } from 'react';
import { FaSignOutAlt, FaUser, FaCog, FaHome } from 'react-icons/fa';
import { FiAlignJustify } from 'react-icons/fi';
import logo from "../../Page/TNS_Energo_logo.png";
import './HeaderAD.css';

export function HeaderAD({ toggleMenu, handleLogout, showMenu, children }) {
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    // Обработчик клика вне меню
    useEffect(() => {
        function handleClickOutside(event) {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setDropdownOpen(false);
            }
        }

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    return (
        <header className="header">
            <button
                className="menu-btn"
                onClick={toggleMenu}
                aria-label="Меню"
                aria-expanded={showMenu}
            >
                <FiAlignJustify size={20} />
            </button>

            <div className="logo-container">
                <img src={logo} alt="Логотип ТНС Энерго" className="logo" />
            </div>

            <div className="header-actions" ref={dropdownRef}>
                <button
                    className="user-btn"
                    onClick={() => setDropdownOpen(!dropdownOpen)}
                    aria-label="Меню пользователя"
                    aria-expanded={dropdownOpen}
                >
                    <FaUser size={18} />
                </button>

                <div className={`menu-dropdown ${dropdownOpen ? 'active' : ''}`}>
                    <div className="menu-item">
                        <FaHome size={16} className="menu-icon" />
                        <span>Главная</span>
                    </div>
                    <div className="menu-item">
                        <FaUser size={16} className="menu-icon" />
                        <span>Профиль</span>
                    </div>
                    <div className="menu-item">
                        <FaCog size={16} className="menu-icon" />
                        <span>Настройки</span>
                    </div>
                    <div className="menu-divider"></div>
                    <div
                        className="menu-item"
                        onClick={handleLogout}
                    >
                        <FaSignOutAlt size={16} className="menu-icon" />
                        <span>Выход</span>
                    </div>
                </div>
            </div>

            {children}
        </header>
    );
}
